    #region Generic Conversions

    static bool INumberBase<Int9T>.TryConvertFromChecked<TOther>(TOther value, out Int9T result)
    {
        if (value is IConvertible conv)
        {
            try
            {
                var intValue = conv.ToInt16(null);
                if (intValue >= MinValueConstant && intValue <= MaxValueConstant)
                {
                    result = Create(intValue);
                    return true;
                }
            }
            catch
            {
                // Fall through to default
            }
        }

        result = default;
        return false;
    }

